var header = "<table><thead><tr><th>Source IP</th><th>Destination IP</th></tr></thead><tbody>";
var data = datasources["Connections"]["data"]["allConnections"]["edges"];
var dataRows = data.map(edge => `<tr><td>${edge.node.sourceIP}</td><td>${edge.node.destIP}</td></tr>`).join('');
var footer = "</tbody></table>";
return header + dataRows + footer;

